// Analizar datos de estado de √°nimo
const moodData = $input.first().json;
const userId = moodData.userId;
const moodLevel = moodData.moodLevel;
const timestamp = moodData.timestamp;

// Obtener historial de estados de √°nimo (simulado)
const moodHistory = [
  { date: '2024-01-01', mood: 4 },
  { date: '2024-01-02', mood: 3 },
  { date: '2024-01-03', mood: moodLevel }
];

// Calcular tendencia
const calculateTrend = (history) => {
  if (history.length < 2) return 'insufficient_data';

  const recent = history.slice(-3);
  const avg = recent.reduce((sum, item) => sum + item.mood, 0) / recent.length;
  const previousAvg = history.slice(-6, -3).reduce((sum, item) => sum + item.mood, 0) / 3;

  if (avg > previousAvg + 0.5) return 'improving';
  if (avg < previousAvg - 0.5) return 'declining';
  return 'stable';
};

// Detectar patrones preocupantes
const detectConcerns = (level, history) => {
  const concerns = [];

  if (level <= 2) concerns.push('low_mood');

  const recentLow = history.slice(-3).filter(item => item.mood <= 2);
  if (recentLow.length >= 2) concerns.push('prolonged_low_mood');

  return concerns;
};

const trend = calculateTrend(moodHistory);
const concerns = detectConcerns(moodLevel, moodHistory);
const needsIntervention = concerns.length > 0;

return {
  userId,
  moodLevel,
  timestamp,
  analysis: {
    trend,
    concerns,
    needsIntervention,
    confidence: Math.random() * 30 + 70, // 70-100%
    recommendation: needsIntervention ? 'immediate_support' : 'continue_monitoring'
  },
  triggerNotification: needsIntervention,
  moodHistory
};

// Generar recomendaciones personalizadas
const data = $input.first().json;
const moodLevel = data.moodLevel;
const concerns = data.analysis.concerns;

const generateRecommendations = (level, concerns) => {
  const recommendations = [];

  if (level <= 2) {
    recommendations.push({
      type: 'immediate',
      action: 'prayer_moment',
      content: 'Toma un momento para orar y entregar tus cargas a Dios',
      verse: 'Filipenses 4:6-7'
    });

    recommendations.push({
      type: 'content',
      action: 'encouraging_verse',
      content: 'Vers√≠culo personalizado de aliento',
      priority: 'high'
    });
  }

  if (concerns.includes('prolonged_low_mood')) {
    recommendations.push({
      type: 'intervention',
      action: 'support_resources',
      content: 'Recursos de apoyo y contactos de ayuda',
      priority: 'urgent'
    });

    recommendations.push({
      type: 'notification',
      action: 'telegram_check_in',
      content: 'Check-in personal v√≠a Telegram',
      schedule: 'within_2_hours'
    });
  }

  if (level >= 4) {
    recommendations.push({
      type: 'reinforcement',
      action: 'gratitude_prompt',
      content: 'Momento de gratitud y reflexi√≥n positiva',
      priority: 'medium'
    });
  }

  return recommendations;
};

const recommendations = generateRecommendations(moodLevel, concerns);

return {
  ...data,
  recommendations,
  interventionPlan: {
    immediate: recommendations.filter(r => r.priority === 'urgent' || r.priority === 'high'),
    followUp: recommendations.filter(r => r.priority === 'medium'),
    monitoring: {
      frequency: concerns.length > 0 ? 'daily' : 'weekly',
      duration: concerns.length > 0 ? '7_days' : '30_days'
    }
  }
};

